import "./App.css";
import { Routes, Route } from "react-router-dom";
import About from "./Components/About";
import Home from "./Components/Home";
import Contacts from "./Components/Contacts";
import AddContacts from "./Components/AddContacts";
import Navbar from "./Components/Navbar";
import "react-datepicker/dist/react-datepicker.css";
import SingleContact from "./Components/SingleContact";
import { v4 as uuidv4 } from "uuid";
import { useContext, useState } from "react";
import EditContacts from "./Components/EditContacts";
import { UsersContext } from "./Components/Contextapi/UserContext";

function App() {
  const contactContext = useContext(UsersContext);

  const [contacts, setContacts] = useState(ourcontacts);

  const deleteContact = (id) => {
    const deleteContact = contacts.filter((contact) => contact.id !== id);
    setContacts(deleteContact);
  };

  const updateContact = (id) => {
    console.log(id);
  };

  const addContact = (contact) => {
    let contactToAdd = {
      id: uuidv4(),
      ...contact,
    };
    setContacts([contactToAdd, ...contacts]);
  };

  return (
    <>
      <Navbar></Navbar>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/about" element={<About />} />
        <Route
          path="/contacts"
          element={
            <Contacts deleteContact={deleteContact} contacts={contacts} />
          }
        />
        <Route
          path="/addcontacts"
          element={<AddContacts addContact={addContact} />}
        />{" "}
        <Route path="/editcontacts/:id" element={<EditContacts />} />
        <Route
          path="/contacts/:id"
          element={
            <SingleContact
              contacts={contacts}
              deleteContact={deleteContact}
              updateContact={updateContact}
            />
          }
        />
      </Routes>
    </>
  );
}

export default App;
